mapper.select:
select staff_id
from web.staff
where staff_id=#{id}

mapper.insert:
INSERT INTO web.staff
	(staff_id
	   , staff_pass
	   , staff_name
	   , staff_pnum
	   , staff_email
	   , memo
	   , password_key
	   , cret_dt
	   , cret_id
	   , updt_dt
	   , updt_id
	)VALUES(
		#{staffId}
	   , #{staffPass}
	   , #{staffName}
	   , #{staffPnum}
	   , #{staffEmail}
	   , #{memo}
	   , #{passwordKey}
	   , NOW()
	   , #{cretId}
	   , NOW()
	   , #{cretId}
	)

mapper.update:
UPDATE web.staff SET
staff_pass=#{password},
staff_name=#{name}, staff_pnum=#{telPhone}, staff_email=#{mail}, memo=#{memo}, active_yn=#{active}, admin_yn=#{admin}, updt_dt=NOW(), updt_id='test' WHERE staff_id=#{id}
